n_protein=#{protein} ,n_fat=#{fat} ,n_fiber=#{fiber} ,n_carbohydrate=#{carbohydrate} ,n_va=#{va} ,n_vb1=#{vb1} ,n_vb2=#{vb2} ,n_niacin=#{niacin} ,
n_ve=#{ve} ,n_Na=#{na} ,n_Ca=#{ca} ,n_Fe=#{fe} ,n_vc=#{vc} ,n_cholesterol=#{cholesterol}
where n_foodId = #{foodId}
